import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

interface PredictionRequest {
  currentSentence: string;
  contextSentences?: string[];
  maxSuggestions?: number;
}

interface PredictionResponse {
  suggestions: {
    text: string;
    confidence: number;
    type: 'temporal' | 'pattern' | 'contextual';
    reasoning?: string;
  }[];
  temporalContext: {
    hourOfDay: number;
    dayOfWeek: number;
    commonAtThisTime: string[];
  };
}

Deno.serve(async (req: Request) => {
  if (req.method !== 'POST') {
    return new Response('Method not allowed', { status: 405 });
  }

  try {
    const supabaseClient = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_ANON_KEY') ?? '',
      {
        global: {
          headers: { Authorization: req.headers.get('Authorization')! },
        },
      }
    );

    const { data: { user } } = await supabaseClient.auth.getUser();
    if (!user) {
      return new Response('Unauthorized', { status: 401 });
    }

    const { currentSentence, contextSentences = [], maxSuggestions = 6 }: PredictionRequest = await req.json();

    const now = new Date();
    const hourOfDay = now.getHours();
    const dayOfWeek = now.getDay();

    // Tokenize current sentence
    const words = currentSentence.trim().toLowerCase().split(/\s+/).filter(Boolean);
    const lastWord = words[words.length - 1] || '';
    const lastTwoWords = words.slice(-2).join(' ');

    console.log('Processing prediction for:', { currentSentence, hourOfDay, dayOfWeek, lastWord, lastTwoWords });

    // 1. Get temporal patterns for current time
    const { data: temporalPatterns } = await supabaseClient
      .from('temporal_patterns')
      .select('sentence_text, usage_count')
      .eq('user_id', user.id)
      .eq('hour_of_day', hourOfDay)
      .eq('day_of_week', dayOfWeek)
      .order('usage_count', { ascending: false })
      .limit(10);

    // 2. Get n-gram patterns
    const { data: trigramPatterns } = await supabaseClient
      .from('sentence_patterns')
      .select('next_word, frequency, temporal_weight')
      .eq('user_id', user.id)
      .eq('pattern_type', 'trigram')
      .eq('pattern_key', lastTwoWords)
      .order('frequency', { ascending: false });

    const { data: bigramPatterns } = await supabaseClient
      .from('sentence_patterns')
      .select('next_word, frequency, temporal_weight')
      .eq('user_id', user.id)
      .eq('pattern_type', 'bigram')
      .eq('pattern_key', lastWord)
      .order('frequency', { ascending: false });

    // 3. Get contextual sentences (similar to current context)
    const { data: contextualSentences } = await supabaseClient
      .from('sentences')
      .select('text, usage_count, hour_of_day, day_of_week')
      .eq('user_id', user.id)
      .order('usage_count', { ascending: false })
      .limit(50);

    // 4. Build suggestions with confidence scoring
    const suggestions = new Map<string, { confidence: number; type: string; reasoning: string }>();

    // Temporal suggestions (high confidence for current time patterns)
    if (temporalPatterns) {
      for (const pattern of temporalPatterns.slice(0, 3)) {
        const confidence = Math.min(0.9, pattern.usage_count * 0.1 + 0.3);
        suggestions.set(pattern.sentence_text, {
          confidence,
          type: 'temporal',
          reasoning: `Often used at ${hourOfDay}:00 on ${['Sun','Mon','Tue','Wed','Thu','Fri','Sat'][dayOfWeek]}`
        });
      }
    }

    // Trigram suggestions (highest confidence for pattern matching)
    if (trigramPatterns && trigramPatterns.length > 0) {
      for (const pattern of trigramPatterns.slice(0, 3)) {
        const baseConfidence = Math.min(0.95, pattern.frequency * 0.15 + 0.4);
        const temporalBoost = pattern.temporal_weight || 1.0;
        const confidence = Math.min(0.98, baseConfidence * temporalBoost);

        const nextSentence = currentSentence + ' ' + pattern.next_word;
        suggestions.set(nextSentence, {
          confidence,
          type: 'pattern',
          reasoning: `Follows "${lastTwoWords}" pattern (${pattern.frequency} times)`
        });
      }
    }

    // Bigram suggestions (medium confidence)
    if (bigramPatterns && bigramPatterns.length > 0) {
      for (const pattern of bigramPatterns.slice(0, 4)) {
        const baseConfidence = Math.min(0.8, pattern.frequency * 0.1 + 0.2);
        const temporalBoost = pattern.temporal_weight || 1.0;
        const confidence = Math.min(0.85, baseConfidence * temporalBoost);

        const nextSentence = currentSentence + ' ' + pattern.next_word;
        if (!suggestions.has(nextSentence)) {
          suggestions.set(nextSentence, {
            confidence,
            type: 'pattern',
            reasoning: `Follows "${lastWord}" pattern (${pattern.frequency} times)`
          });
        }
      }
    }

    // Contextual suggestions (lower confidence, but good for discovery)
    if (contextualSentences) {
      for (const sentence of contextualSentences.slice(0, 10)) {
        if (!suggestions.has(sentence.text) && sentence.text.toLowerCase().includes(lastWord)) {
          const timeMatch = sentence.hour_of_day === hourOfDay ? 1.2 : 1.0;
          const confidence = Math.min(0.7, sentence.usage_count * 0.05 + 0.1) * timeMatch;

          suggestions.set(sentence.text, {
            confidence,
            type: 'contextual',
            reasoning: `Contains "${lastWord}" (used ${sentence.usage_count} times)`
          });
        }
      }
    }

    // Sort by confidence and limit results
    const sortedSuggestions = Array.from(suggestions.entries())
      .map(([text, data]) => ({ text, ...data }))
      .sort((a, b) => b.confidence - a.confidence)
      .slice(0, maxSuggestions);

    // Get common sentences for this time
    const commonAtThisTime = temporalPatterns?.slice(0, 3).map(p => p.sentence_text) || [];

    const response: PredictionResponse = {
      suggestions: sortedSuggestions,
      temporalContext: {
        hourOfDay,
        dayOfWeek,
        commonAtThisTime
      }
    };

    console.log('Generated predictions:', response);

    return new Response(JSON.stringify(response), {
      headers: {
        'Content-Type': 'application/json',
        'Connection': 'keep-alive'
      }
    });

  } catch (error) {
    console.error('Prediction error:', error);
    return new Response(
      JSON.stringify({ error: 'Failed to generate predictions', details: error.message }),
      { 
        status: 500,
        headers: { 'Content-Type': 'application/json' }
      }
    );
  }
});

import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

interface StoreSentenceRequest {
  sentence: string;
  wordIds?: string[];
}

Deno.serve(async (req: Request) => {
  if (req.method !== 'POST') {
    return new Response('Method not allowed', { status: 405 });
  }

  try {
    const supabaseClient = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_ANON_KEY') ?? '',
      {
        global: {
          headers: { Authorization: req.headers.get('Authorization')! },
        },
      }
    );

    const { data: { user } } = await supabaseClient.auth.getUser();
    if (!user) {
      return new Response('Unauthorized', { status: 401 });
    }

    const { sentence, wordIds = [] }: StoreSentenceRequest = await req.json();

    if (!sentence || sentence.trim().length === 0) {
      return new Response('Invalid sentence', { status: 400 });
    }

    const now = new Date();
    const hourOfDay = now.getHours();
    const dayOfWeek = now.getDay();
    const words = sentence.trim().toLowerCase().split(/\s+/).filter(Boolean);
    const wordCount = words.length;

    console.log('Storing sentence:', { sentence, hourOfDay, dayOfWeek, wordCount });

    // 1. Store or update the sentence
    const { data: existingSentence } = await supabaseClient
      .from('sentences')
      .select('id, usage_count')
      .eq('user_id', user.id)
      .eq('text', sentence)
      .single();

    if (existingSentence) {
      // Update existing sentence
      await supabaseClient
        .from('sentences')
        .update({
          usage_count: existingSentence.usage_count + 1,
          last_used_at: now.toISOString(),
          hour_of_day: hourOfDay,
          day_of_week: dayOfWeek
        })
        .eq('id', existingSentence.id);
    } else {
      // Insert new sentence
      await supabaseClient
        .from('sentences')
        .insert({
          user_id: user.id,
          text: sentence,
          word_count: wordCount,
          hour_of_day: hourOfDay,
          day_of_week: dayOfWeek,
          usage_count: 1,
          last_used_at: now.toISOString()
        });
    }

    // 2. Store temporal pattern
    const { data: existingTemporal } = await supabaseClient
      .from('temporal_patterns')
      .select('id, usage_count')
      .eq('user_id', user.id)
      .eq('sentence_text', sentence)
      .eq('hour_of_day', hourOfDay)
      .eq('day_of_week', dayOfWeek)
      .single();

    if (existingTemporal) {
      await supabaseClient
        .from('temporal_patterns')
        .update({
          usage_count: existingTemporal.usage_count + 1,
          last_used_at: now.toISOString()
        })
        .eq('id', existingTemporal.id);
    } else {
      await supabaseClient
        .from('temporal_patterns')
        .insert({
          user_id: user.id,
          sentence_text: sentence,
          hour_of_day: hourOfDay,
          day_of_week: dayOfWeek,
          usage_count: 1,
          last_used_at: now.toISOString()
        });
    }

    // 3. Build and store n-gram patterns
    const patterns = [];

    // Unigrams
    for (const word of words) {
      patterns.push({
        pattern_type: 'unigram',
        pattern_key: '',
        next_word: word
      });
    }

    // Bigrams
    for (let i = 0; i < words.length - 1; i++) {
      patterns.push({
        pattern_type: 'bigram',
        pattern_key: words[i],
        next_word: words[i + 1]
      });
    }

    // Trigrams
    for (let i = 0; i < words.length - 2; i++) {
      patterns.push({
        pattern_type: 'trigram',
        pattern_key: `${words[i]} ${words[i + 1]}`,
        next_word: words[i + 2]
      });
    }

    // Calculate temporal weight based on current time patterns
    const temporalWeight = 1.0 + (Math.sin((hourOfDay / 24) * 2 * Math.PI) * 0.2);

    // Store or update patterns
    for (const pattern of patterns) {
      const { data: existingPattern } = await supabaseClient
        .from('sentence_patterns')
        .select('id, frequency')
        .eq('user_id', user.id)
        .eq('pattern_type', pattern.pattern_type)
        .eq('pattern_key', pattern.pattern_key)
        .eq('next_word', pattern.next_word)
        .single();

      if (existingPattern) {
        await supabaseClient
          .from('sentence_patterns')
          .update({
            frequency: existingPattern.frequency + 1,
            temporal_weight: temporalWeight,
            updated_at: now.toISOString()
          })
          .eq('id', existingPattern.id);
      } else {
        await supabaseClient
          .from('sentence_patterns')
          .insert({
            user_id: user.id,
            pattern_type: pattern.pattern_type,
            pattern_key: pattern.pattern_key,
            next_word: pattern.next_word,
            frequency: 1,
            temporal_weight: temporalWeight
          });
      }
    }

    console.log('Successfully stored sentence and patterns');

    return new Response(
      JSON.stringify({ 
        success: true, 
        patternsCreated: patterns.length,
        temporalContext: { hourOfDay, dayOfWeek }
      }),
      {
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive'
        }
      }
    );

  } catch (error) {
    console.error('Store sentence error:', error);
    return new Response(
      JSON.stringify({ error: 'Failed to store sentence', details: error.message }),
      { 
        status: 500,
        headers: { 'Content-Type': 'application/json' }
      }
    );
  }
});

