-- Create user_patterns table for advanced AI
CREATE TABLE IF NOT EXISTS user_patterns (
  id SERIAL PRIMARY KEY,
  pattern_type VARCHAR(50) NOT NULL,
  pattern_key TEXT NOT NULL,
  frequency INTEGER DEFAULT 1,
  metadata JSONB,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  UNIQUE(pattern_type, pattern_key)
);

-- Create indexes for better performance
CREATE INDEX IF NOT EXISTS idx_user_patterns_type_key ON user_patterns(pattern_type, pattern_key);
CREATE INDEX IF NOT EXISTS idx_user_patterns_frequency ON user_patterns(frequency DESC);

-- Enable RLS
ALTER TABLE user_patterns ENABLE ROW LEVEL SECURITY;

-- Create RLS policies (allow all operations for now since this is a single-user app)
CREATE POLICY "Allow all operations on user_patterns" ON user_patterns
  FOR ALL USING (true) WITH CHECK (true);
