-- Create sentences table to store all typed sentences with temporal context
CREATE TABLE sentences (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  text TEXT NOT NULL,
  word_count INTEGER NOT NULL DEFAULT 0,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  hour_of_day INTEGER NOT NULL, -- 0-23
  day_of_week INTEGER NOT NULL, -- 0-6 (Sunday=0)
  usage_count INTEGER DEFAULT 1,
  last_used_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Create sentence patterns table for n-gram analysis
CREATE TABLE sentence_patterns (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  pattern_type VARCHAR(20) NOT NULL, -- 'unigram', 'bigram', 'trigram'
  pattern_key TEXT NOT NULL, -- the n-gram key
  next_word TEXT NOT NULL, -- the predicted next word
  frequency INTEGER DEFAULT 1,
  temporal_weight FLOAT DEFAULT 1.0, -- weight based on time patterns
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Create temporal usage patterns table
CREATE TABLE temporal_patterns (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  sentence_text TEXT NOT NULL,
  hour_of_day INTEGER NOT NULL,
  day_of_week INTEGER NOT NULL,
  usage_count INTEGER DEFAULT 1,
  last_used_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Create indexes for performance
CREATE INDEX idx_sentences_user_id ON sentences(user_id);
CREATE INDEX idx_sentences_temporal ON sentences(user_id, hour_of_day, day_of_week);
CREATE INDEX idx_sentences_text ON sentences(user_id, text);
CREATE INDEX idx_sentence_patterns_user_pattern ON sentence_patterns(user_id, pattern_type, pattern_key);
CREATE INDEX idx_temporal_patterns_user_time ON temporal_patterns(user_id, hour_of_day, day_of_week);

-- Enable RLS
ALTER TABLE sentences ENABLE ROW LEVEL SECURITY;
ALTER TABLE sentence_patterns ENABLE ROW LEVEL SECURITY;
ALTER TABLE temporal_patterns ENABLE ROW LEVEL SECURITY;

-- Create RLS policies
CREATE POLICY "Users can manage their own sentences" ON sentences
  FOR ALL USING (user_id = auth.uid());

CREATE POLICY "Users can manage their own sentence patterns" ON sentence_patterns
  FOR ALL USING (user_id = auth.uid());

CREATE POLICY "Users can manage their own temporal patterns" ON temporal_patterns
  FOR ALL USING (user_id = auth.uid());

