-- Create user_patterns table for advanced AI
CREATE TABLE IF NOT EXISTS user_patterns (
  id SERIAL PRIMARY KEY,
  pattern_type VARCHAR(50) NOT NULL,
  pattern_key TEXT NOT NULL,
  frequency INTEGER DEFAULT 1,
  metadata JSONB,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  UNIQUE(pattern_type, pattern_key)
);

-- Create indexes for better performance
CREATE INDEX IF NOT EXISTS idx_user_patterns_type_key ON user_patterns(pattern_type, pattern_key);
CREATE INDEX IF NOT EXISTS idx_user_patterns_frequency ON user_patterns(frequency DESC);

-- Enable RLS
ALTER TABLE user_patterns ENABLE ROW LEVEL SECURITY;

-- Create RLS policies (allow all operations for now since this is a single-user app)
CREATE POLICY "Allow all operations on user_patterns" ON user_patterns
  FOR ALL USING (true) WITH CHECK (true);

-- Create TTS settings table
CREATE TABLE IF NOT EXISTS tts_settings (
  id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id uuid REFERENCES auth.users(id),
  voice_identifier text NOT NULL DEFAULT 'default',
  voice_name text NOT NULL DEFAULT 'Default Voice',
  language text NOT NULL DEFAULT 'en-US',
  pitch real DEFAULT 1.0,
  rate real DEFAULT 1.0,
  created_at timestamptz DEFAULT now(),
  updated_at timestamptz DEFAULT now()
);

-- Enable RLS
ALTER TABLE tts_settings ENABLE ROW LEVEL SECURITY;

-- Create RLS policies
CREATE POLICY "Users can view their own TTS settings" ON tts_settings FOR SELECT USING (user_id = auth.uid());
CREATE POLICY "Users can insert their own TTS settings" ON tts_settings FOR INSERT WITH CHECK (user_id = auth.uid());
CREATE POLICY "Users can update their own TTS settings" ON tts_settings FOR UPDATE USING (user_id = auth.uid());
CREATE POLICY "Users can delete their own TTS settings" ON tts_settings FOR DELETE USING (user_id = auth.uid());

-- Create unique constraint to ensure one setting per user
CREATE UNIQUE INDEX IF NOT EXISTS tts_settings_user_id_unique ON tts_settings(user_id);

-- Add more structured AI preferences for personalized predictions
ALTER TABLE ai_preferences ADD COLUMN IF NOT EXISTS category VARCHAR(50) DEFAULT 'general';
ALTER TABLE ai_preferences ADD COLUMN IF NOT EXISTS display_order INTEGER DEFAULT 0;
ALTER TABLE ai_preferences ADD COLUMN IF NOT EXISTS is_active BOOLEAN DEFAULT true;

-- Create index for better performance
CREATE INDEX IF NOT EXISTS idx_ai_preferences_category ON ai_preferences(category, is_active);
CREATE INDEX IF NOT EXISTS idx_ai_preferences_user_category ON ai_preferences(user_id, category, is_active);

-- Add unique constraint to preference_key
ALTER TABLE ai_preferences ADD CONSTRAINT ai_preferences_preference_key_unique UNIQUE (preference_key);

-- Clear old data that might conflict
DELETE FROM ai_preferences WHERE preference_key IN ('colour', 'food', 'animal', 'activity', 'place', 'weather');

